    "MATH 537": {
        "prereqs": [],
        "coreqs": [],
        "restricts": [],
        "text": {
            "notes": "<p>Prerequisite(s): <a href=\"https://mcgill.ca/study/2019-2020/courses/math-325\" target=\"_blank\">MATH 325</a>, <a href=\"https://mcgill.ca/study/2019-2020/courses/math-376\" target=\"_blank\">MATH 376</a>, and <a href=\"https://mcgill.ca/study/2019-2020/courses/comp-202\" target=\"_blank\">COMP 202</a> or <a href=\"https://mcgill.ca/study/2019-2020/courses/comp-204\" target=\"_blank\">COMP 204</a> or <a href=\"https://mcgill.ca/study/2019-2020/courses/comp-208\" target=\"_blank\">COMP 208</a> or equivalent.</p>\n\n<p>Restriction(s): Not open to students who have taken <a href=\"https://mcgill.ca/study/2019-2020/courses/math-437\" target=\"_blank\">MATH 437</a>.</p>\n\n<p>This course is offered in the winter semester.</p>\n",
            "name": "Honours Mathematical Models in Biology",
            "title": "<a href=\"https://mcgill.ca/study/2019-2020/courses/math-537\" target=\"_blank\">MATH 537</a> Honours Mathematical Models in Biology (4 credits)",
            "overview": "Mathematics &amp; Statistics (Sci) : The formulation and treatment of realistic mathematical models describing biological phenomena through such qualitative and quantitative mathematical techniques as local and global stability theory, bifurcation analysis, phase plane analysis, and numerical simulation.  Concrete and detailed examples will be drawn from molecular, cellular and population biology and mammalian physiology.",
            "terms": "Terms: Winter 2020",
            "instructors": "Instructors: Anmar Khadra (Winter)"
        }
    },
